sources.styles/utils/reset.scss:
html, body, div, span, applet, object, iframe,
h1, h2, h3, h4, h5, h6, p, blockquote, pre,
a, abbr, acronym, address, big, cite, code,
del, dfn, em, font, img, ins, kbd, q, s, samp,
small, strike, strong, sub, sup, tt, var,
dl, dt, dd, ol, ul, li,
fieldset, form, label, legend,
table, caption, tbody, tfoot, thead, tr, th, td {
	margin: 0;
	padding: 0;
	border: 0;
	outline: 0;
	font-weight: inherit;
	font-style: inherit;
	font-size: 100%;
	font-family: inherit;
	vertical-align: baseline;
}
/* remember to define focus styles! */
:focus {
	outline: 0;
}
body {
	line-height: 1;
	color: black;
	background: white;
}
ol, ul {
	list-style: none;
}

sources.styles/utils/animations.scss:
@keyframes text-focus-in {
  0% {
    filter: blur(12px);
    opacity: 0;
  }
  100% {
    filter: blur(0px);
    opacity: 1;
  }
}

@keyframes swing-in-top-fwd {
  0% {
    transform: rotateX(-100deg);
    transform-origin: top;
    opacity: 0;
  }
  100% {
    transform: rotateX(0deg);
    transform-origin: top;
    opacity: 1;
  }
}

sources.styles/blocks/page.scss:
.page {
  font-family: "Cinzel", serif;
  font-size: 16px;
  color: $c-mainDark;
  scroll-behavior: smooth;

  &__body {
    margin: 0;

    &--with-menu {
      overflow: hidden;
    }
  }

  &__menu {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;

    transform: translateX(-100%);
    transition: transform 0.5s;

    &:target {
      transform: translateX(0);
    }
  }

  &__section {
    margin-bottom: 100px;

    @include onTablet {
      margin-bottom: 140px;
    }

    @include onDesktop {
      margin-bottom: 140px;
    }

    &-title {
      font-weight: 700;
      font-size: 32px;
      line-height: 32px;
      text-align: center;
      letter-spacing: -1.5px;
      color: $c-mainDark;

      @include onTablet {
        font-size: 48px;
        line-height: 48px;
      }

      @include onDesktop {
        font-size: 48px;
        line-height: 48px;
      }
    }
  }
}

sources.styles/blocks/container.scss:
.container {
  margin: 0 auto;
  max-width: 1020px;
  padding: 0 20px;

  @include onTablet {
    padding: 0 72px;
    max-width: 600px;
    margin: 0 auto;
  }

  @include onDesktop {
    padding: 0 120px;
  }
}

sources.styles/blocks/top-actions.scss:
.top-actions {
  --icon-width: 18px;
  display: grid;
  grid-template-columns: 1fr var(--icon-width) var(--icon-width);
  column-gap: 30px;
  // justify-content: space-between;
  align-items: center;

  animation: swing-in-top-fwd 1s cubic-bezier(0.175, 0.885, 0.320, 1.275) 1s both;

  @include onTablet {
    --icon-width: 24px;
    column-gap: 63px;
  }

  @include onDesktop {
    --icon-width: 24px;
    column-gap: 63px;
  }
}

sources.styles/blocks/header.scss:
.header {
  margin-bottom: 100px;

  @include onTablet {
    margin-bottom: 140px;
  }

  @include onDesktop {
    margin-bottom: 140px;
  }

  background-size: cover;
  background-position: center;
  background-image: url(../images/photos/header_bg.jpeg);

  &__content {
    display: flex;
    flex-direction: column;

    box-sizing: border-box;
    height: 100vh;
    padding: $headerTopPadding 0 0;

    @include onTablet {
      padding: 32px 0 0;
    }

    @include onDesktop {
      padding: 32px 0 0;
    }
  }

  &__title {
    padding: 129px 0 0;
    font-weight: 700;
    font-size: 40px;
    line-height: 100%;
    text-align: center;
    letter-spacing: -1.5px;
    color: #FFFFFF;

    cursor: pointer;

    @include onTablet {
      padding: 159px 0 0;
      font-size: 64px;
      line-height: 64px;
    }

    @include onDesktop {
      padding: 103px 0 0;
      font-size: 80px;
      line-height: 80px;
    }

    animation: text-focus-in 1.5s cubic-bezier(0.550, 0.085, 0.680, 0.530) both;

    transition: transform 790ms ease-out;

    &:hover {
      transform: translate(0px, 0px) scale(1.1, 1.1);
    }
  }

  &__call {
    text-decoration: none;

    @include onDesktop {
      &::before {
        margin-bottom: 8px;
        content: "The Met Fifth Avenue\A+1 212-535-7710";
        white-space: pre;
        text-align: end;
        opacity: 0;

        position: fixed;
        top: -20px;
        transform: translate(-100%);

        display: block;
        width: max-content;
        color: #FFFFFF;

        padding-right: 20px;

        font-weight: 700;
        font-size: 16px;
        line-height: 27px;
        text-transform: uppercase;

        pointer-events: none;
        transition-duration: 0.3s;
      }

      &::after {
        content: "The Met Cloisters \A +1 212-923-3700";
        opacity: 0;
        white-space: pre;
        text-align: end;

        position: fixed;
        top: 40px;
        transform: translate(-100%);

        display: block;
        width: max-content;
        color: #FFFFFF;

        padding-right: 20px;

        font-weight: 700;
        font-size: 16px;
        line-height: 27px;
        text-transform: uppercase;

        pointer-events: none;
        transition-duration: 0.3s;
      }

      &:hover::before {
        opacity: 1;
        pointer-events: all;
      }

      &:hover::after {
        opacity: 1;
        pointer-events: all;
      }
    }
  }
}

sources.styles/blocks/logo.scss:
.logo {
  &__image {
    height: 46px;

    @include onTablet {
      height: 65px;
    }

    @include onDesktop {
      height: 65px;
    }

    transition: transform 500ms ease-out;

    &:hover {
      transform: translate(0px, 0px) scale(1.1, 1.1);
    }
