sources.styles/blocks/icon.scss:
.icon {
  display: block;

  width: 100%;
  padding-bottom: 100%;

  background-size: contain;
  background-position: center;
  background-repeat: no-repeat;

  &--menu {
    background-image: url(../images/menu.svg);
    transition: background-image 0.2s ease-in;

    &:hover {
      background-image: url(../images/menu_hover.svg);
    }
  }

  &--phone {
    background-image: url(../images/phone.svg);
  }

  &--cross {
    background-image: url(../images/cross.svg);

    transition: transform 300ms ease-out;

    &:hover {
      transform: rotate(360deg) translate(0px, 0px);
    }
  }
}

sources.styles/blocks/menu.scss:
.menu {
  background-color: $c-accentMain;

  &__top {
    margin-bottom: 48px;
  }

  &__content {
    box-sizing: border-box;
    height: 100vh;
    padding: $headerTopPadding 0;

    @include onTablet {
      padding: 32px 0 0;
    }

    @include onDesktop {
      padding: 32px 0 0;
    }
  }

  &__list {
    list-style: none;
    margin: 0;
    padding: 0;
    margin: 0 0 48px;
  }

  &__item {
    margin-bottom: 24px;

    &:last-child {
      margin-bottom: 0;
    }
  }

  &__link {
    display: inline-block;
    border-bottom: 1px solid transparent;

    padding-bottom: 8px;
    font-weight: 700;
    font-size: 22px;
    line-height: 22px;
    letter-spacing: 2px;
    text-transform: uppercase;

    color: #FFFFFF;
    text-decoration: none;

    transition: border-color 0.3s;

    &:hover {
      border-color: #fff;
    }
  }

  &__contacts {
    padding-bottom: 8px;

    &-item {
      padding-bottom: 8px;
    }

    &-link {
      font-weight: 700;
      font-size: 16px;
      line-height: 27px;
      text-transform: uppercase;

      color: #FFFFFF;
      text-decoration: none;
    }

    &-cta {
      width: max-content;
      font-weight: 700;
      font-size: 12px;
      line-height: 16px;
      letter-spacing: 2px;
      text-transform: uppercase;

      color: #FFFFFF;
      text-decoration: none;

      border-bottom: 1px solid #fff;
    }
  }
}

sources.styles/blocks/button.scss:
.button {
  display: flex;
  justify-content: center;
  align-items: center;
  box-sizing: border-box;
  width: 100%;
  height: 56px;

  text-decoration: none;
  color: #fff;
  background-color: $c-accentMain;
  font-weight: 700;
  line-height: 27px;
  border: none;
  cursor: pointer;

  transition: all 0.8s;

  &:hover {
    background-color: #ae1c23;
    transform: translate(0, 0) scale(1.03, 1.03);
  }

  &:active {
    background-color: #eb343d;
  }
}

sources.styles/blocks/grid.scss:
.grid {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  column-gap: 20px;

  @include onTablet {
    grid-template-columns: repeat(6, 1fr);
    column-gap: 24px;
  }

  @include onDesktop {
    grid-template-columns: repeat(12, 1fr);
    column-gap: 24px;
  }

  &__three-columns {
    display: grid;
    column-gap: 20px;
    grid-template-columns: repeat(3, 1fr);

    @include onTablet {
      grid-template-columns: repeat(2, 1fr);
    }

    @include onDesktop {
      grid-template-columns: repeat(3, 1fr);
      column-gap: 24px;
    }
  }

  &--tablet {
    display: block;

    @include onTablet {
      display: grid;
    }
  }

  &--desktop {
    @include onDesktop {
      display: grid;
    }
  }

  &__item {
    @for $start from 1 through 12 {
      @for $end from $start through 12 {
        &--#{$start}-#{$end} {
          grid-column: #{$start} / #{$end + 1};
        }

        &--desktop-#{$start}-#{$end} {
          @include onDesktop {
            grid-column: #{$start} / #{$end + 1};
          }
        }

        &--tablet-#{$start}-#{$end} {
          @include onTablet {
            grid-column: #{$start} / #{$end + 1};
          }
        }
      }
    }

    &-hide {
      @include onTablet {
        display: none;
      }
    }
  }

  &__time-days {
    grid-template-columns: auto 1fr;
    column-gap: 25px;
  }

  &__row-1 {
    @include onTablet {
      grid-template-rows: 1fr;
      grid-auto-flow: column;
    }
  }
}

sources.styles/blocks/museum-hours.scss:
.museum-hours {
  overflow: hidden;

  &__title {
    margin-bottom: 32px;

    text-align: left;
  }

  &__subtitle {
    font-weight: 700;
    font-size: 12px;
    line-height: 140%;
    color: $c-accentMain;
  }

  &__item {
    margin-bottom: 24px;
  }

  &__where {
    margin-bottom: 16px;

    font-weight: 700;
    font-size: 24px;
    line-height: 30px;
  }

  &__when {
    margin-bottom: 16px;

    &:last-child {
      margin-bottom: 0;
    }

    &-time {
      margin-bottom: 8px;

      font-weight: 700;
      font-size: 16px;
      line-height: 27px;
    }

    &-days {
      margin-bottom: 8px;
      margin-block-start: 0;
      margin-block-end: 0;

      font-size: 16px;
      line-height: 27px;
    }
  }

  &__closed-on {
    &-title {
      font-weight: 700;
      font-size: 16px;
      line-height: 27px;
    }

    &-info {
      font-size: 16px;
      line-height: 27px;
    }
  }
}

sources.styles/blocks/now-on-view.scss:
