sources.styles/blocks/exhibition.scss:
.exhibition {
  &__photo-container {
    position: relative;
    margin-bottom: 24px;

    box-sizing: border-box;
    width: 100%;
    max-height: 256px;
    overflow: hidden;

    &::after {
      content: "";
      display: block;
      padding-bottom: 100%;
    }

    @include onTablet {
      height: 186px;
      padding-bottom: 0;
    }

    @include onDesktop {
      height: 320px;
      padding-bottom: 0;
    }
  }

  &__photo {
    position: absolute;
    width: 100%;
    height: 100%;
    object-fit: cover;
    object-position: center;

    cursor: pointer;

    transition: transform 800ms ease-out;

    &:hover {
      transform: translate(0, 0) scale(1.04, 1.04);
    }
  }

  &__description {
    &-title {
      font-weight: 700;
      line-height: 27px;
    }
  }
}

sources.styles/blocks/explore-online.scss:
.explore-online {
  overflow: hidden;

  &__title {
    margin-bottom: 40px;

    @include onTablet {
      margin-bottom: 48px;
    }

    @include onDesktop {
      margin-bottom: 48px;
    }
  }

  &__link {
    display: block;
    margin-bottom: 4px;

    font-weight: 700;
    font-size: 24px;
    line-height: 140%;

    text-align: center;
  }

  &__description {
    font-family: "Roboto", serif;
    font-weight: 400;
    font-size: 16px;
    line-height: 27px;
    text-align: center;
  }
  &__photo {
    margin-bottom: 24px;

    width: 100%;

    cursor: pointer;

    transition: transform 286ms ease-out;

    &:hover {
      transform: translate(0, 0) scale(1.04, 1.04);
    }
  }

  &__item {
    margin-bottom: 48px;

    @include onDesktop {
      margin-bottom: 0;
    }
  }
}

sources.styles/blocks/contact-us.scss:
.contact-us {
  &__title {
    margin-bottom: 48px;
  }

  &__field {
    margin: 0 0 16px;

    &--last {
      margin-bottom: 32px;
    }
  }

  &__grid {


    &-contacts {
      @include onTablet {
        grid-template-columns: 1fr 1fr;
        display: grid;
        grid-column: 1 / 4;
      }

      @include onDesktop {
        display: grid;
        grid-column: 1 / 8;
      }
    }
  }
}

