sources.styles/blocks/form-field.scss:
.form-field {
  box-sizing: border-box;
  display: block;

  width: 100%;
  height: 48px;
  padding: 14px 16px;

  background-color: #F8F8FA;
  border: 1px solid transparent;

  font-family: inherit;
  font-size: 14px;

  &::placeholder {
    font-size: 14px;
    line-height: 20px;
    color: #7E7E83;
  }

  &:hover {
    border-color: #D5D7DE;
  }

  &:focus {
    border-color: $c-mainDark;
    outline: none;
  }

  &--textarea {
    height: 144px;
    resize: none;
  }
}

